import React, { useState } from "react";
import { Button } from "antd";
import "./Dashboad.css";

function Dashboard() {
  const [selectedTab, setSelectedTab] = useState("Today");

  const handleTabClick = (tab) => {
    setSelectedTab(tab);
  };

  return (
    <div className="Dashboard">
      <div className="Button-container">
        <Button
          type={selectedTab === "Today" ? "primary" : "default"}
          onClick={() => handleTabClick("Today")}
          className={` ${selectedTab === "Today" ? "Dashboard-Today-btn-active" : "Dashboard-Today-btn-inactive"}`}
        >
          Today
        </Button>
        <Button
          type={selectedTab === "Overall" ? "primary" : "default"}
          onClick={() => handleTabClick("Overall")}
          className={` ${selectedTab === "Overall" ? "Dashboard-Today-btn-active" : "Dashboard-Today-btn-inactive"}`}
        >
          Overall
        </Button>
      </div>

      {/* Render content based on the selected tab */}
      {selectedTab === "Today" && <div className="Today-content">Today Content</div>}
      {selectedTab === "Overall" && <div className="Overall-content">Overall Content</div>}
    </div>
  );
}

export default Dashboard;
