import React, { useState } from "react";
import "./Dashboad.css";

function Dashboad() {
  const [selectedTab, setSelectedTab] = useState("Today");

  const handleTabClick = (tab) => {
    setSelectedTab(tab);
  };

  return (
    <div className="Dashboad">
      <div className="Button-container">
        <button
          className={selectedTab === "Today" ? "active" : ""}
          onClick={() => handleTabClick("Today")}
        >
          Today
        </button>
        <button
          className={selectedTab === "Overall" ? "active" : ""}
          onClick={() => handleTabClick("Overall")}
        >
          Overall
        </button>
      </div>

      {/* Render content based on the selected tab */}
      {selectedTab === "Today" && <div className="Today-content">Today Content</div>}
      {selectedTab === "Overall" && <div className="Overall-content">Overall Content</div>}
    </div>
  );
}

export default Dashboad;
